import java.util.Arrays;

public class Solution {
    public static void main(String[] args) {
        String s = "";
        String t = "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys";
        System.out.println(new Solution().numDistinct1(s, t));
//        System.out.println(new Solution().numDistinct2(s, t));
    }

    private int m, n;
    private char[] cs, ct;
    private int[][] memo;

    public int numDistinct1(String s, String t) {
        int m = s.length(), n = t.length();
        char[] cs = s.toCharArray(), ct = t.toCharArray();
        int[][] nums = new int[m + 1][n + 1];
        for (int i = 0; i <= m; i++) {
            nums[i][0] = 1;
        }
        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                if (cs[i - 1] != ct[j - 1]) {
                    nums[i][j] = nums[i - 1][j];
                } else {
                    nums[i][j] = nums[i - 1][j - 1] + nums[i - 1][j];
                }
            }
        }
        return nums[m][n];
    }

    public int numDistinct2(String s, String t) {
        m = s.length();
        n = t.length();
        cs = s.toCharArray();
        ct = t.toCharArray();
        memo = new int[m + 1][n + 1];
        for (int[] num : memo) {
            Arrays.fill(num, -1);
        }
        return solve(0, 0);

    }

    private int solve(int i, int j) {
        if (i == m || j == n) {
            if (i == m && j == n) {
                return 1;
            }
            return 0;
        }
        if (memo[i][j] != -1) {
            return memo[i][j];
        }
        if (cs[i] == ct[j]) {
            memo[i][j] = solve(i + 1, j + 1) + solve(i + 1, j);
        } else {
            memo[i][j] = solve(i + 1, j);
        }
        return memo[i][j];
    }
}